import React, { useRef } from 'react'

const App = () => {
  const fname = useRef()
  const lname = useRef()
  const emailaddress = useRef()

  return (
    <>
      <h1>Hello useRef with single state and multiple fields</h1>
      <div><input value='firstname' name='firstname' ref={fname} /></div>
      <div><input value='lastname' name='lastname' ref={lname} /></div>
      <div><input value='email' name='email' ref={emailaddress} /></div>
      <button type='button'>SUBMIT</button>
    </>
  )
}

export default App



