import React, { ususeRef } from 'react'

const App15 = () => {
    const fname = useRef()
    const lname = useRef()

    const [data, setdata] = useState([])

    const arr = JSON.parse(localStorage.getItem("detail")) || []

    const submit_handle = () => {
        const input = {
            fname: fname.current.value,
            lname: fname.current.value,
        }
        arr.push(input)
        localStorage.setItem("detail",arr)
        setdata([...arr])
    }
    return (
    <>
    <input type="text" name='fname' ref={fname}/>
    <input type="text" name='lname' ref={lname}/>
    <button type='button' onClick={submit_handle}>Submit</button>
    </>
  )
}

export default App15
