import axios from 'axios'

export const get_API = async (url,endpoint) => {
    return axios.get(url + endpoint).then((res)=>{
        return res
    }).catch((err)=>{
        return err
    })
}

export const post_API = async (url,endpoint) => {
    return axios.post(url + endpoint).then((res)=>{
        return res
    }).catch((err)=>{
        return err
    })
}
