import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react'

const App10 = () => {
    const [data,setdata] = useState([])
    useEffect(()=>{
      get_API();
    },[])
    const get_API = () => {
        axios.get("http://localhost:3001/posts").then((res)=>{
            setdata(res.data)
        })
    }
    const post_API = () => {
        const input_data = {
            title:title.current.value,
            author:author.current.value
        }
        axios.post("http://localhost:3001/posts",input_data).then((res)=>{
            setdata([...data,res.data])
        })
    }
    const delete_API = (id) => {
        axios.delete(`http://localhost:3001/posts/${id}`).then((res)=>{
            setdata([...data,res.data])
        })
    }
  const title = useRef()
  const author = useRef()
  const submit_handler = () => {
    post_API()
  }
  const delete_handler = () => {
    delete_API()
  }
  return (
    <>
    <input type='text' name='title' ref={title}/>
    <input type='text' name='author' ref={author}/>
    <button onClick={submit_handler}>Add</button>
    {data.map((val_,ind_)=>{
        return(
            <>
            <h1>{val_.id}</h1>
            <h1>{val_.title}</h1>
            <h1>{val_.author}</h1>
            <button onClick={delete_handler}>Delete</button>
            </>
        )
    })}
    </>
  )
}

export default App10
